DROP TABLE IF EXISTS `sys_moments`;
CREATE TABLE `sys_moments` (
  `id` varchar(64) NOT NULL,
  `sys_uid` varchar(64) NOT NULL,
  `sys_cid` varchar(64) NOT NULL,
  `title` varchar(64) NOT NULL,
  `image` varchar(128) default NULL,
  `content` text NOT NULL,
  `time` datetime default NULL,
  `amountOfReading` int(11) default NULL,
  `isdelete` int(11) NOT NULL default '0',
  `slogan` varchar(255) default NULL COMMENT '标语',
  `transpondBy` varchar(255) default NULL COMMENT '转发自',
  PRIMARY KEY  (`id`,`sys_uid`,`sys_cid`),
  KEY `sys_uid_FK` (`sys_uid`),
  KEY `sys_moment_id` USING BTREE (`id`),
  KEY `sys_moment_title` USING BTREE (`title`),
  KEY `sys_moments_cid_FK` (`sys_cid`),
  KEY `sys_moments_isdelete` (`isdelete`),
  CONSTRAINT `sys_moments_cid_FK` FOREIGN KEY (`sys_cid`) REFERENCES `sys_category` (`id`),
  CONSTRAINT `sys_uid_FK` FOREIGN KEY (`sys_uid`) REFERENCES `sys_user` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

