DROP TRIGGER IF EXISTS `user_insert`;
DELIMITER ;;
CREATE TRIGGER `user_insert` AFTER INSERT ON `sys_user` FOR EACH ROW insert into sys_user_role(id,sys_user_id,sys_role_id)
values(null,NEW.id,'2')
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `user_delete`;
DELIMITER ;;
CREATE TRIGGER `user_delete` BEFORE DELETE ON `sys_user` FOR EACH ROW delete from 
sys_user_role
where sys_user_id = OLD.id
;;
DELIMITER ;
